UPDATE data_element SET max_length = 50, validation_expression = '^[\\S](.{0,48})(\\S)$', validation_message = 'Must be up to 50 characters long, not have leading or trailing spaces and not blank', tooltip = 'The name of the merchant site. Appears on receipt. Max 50 chars.' where name = 'name';
UPDATE data_element SET max_length = 50, validation_expression = '^[\\S](.{0,48})(\\S)$', validation_message = 'Must be up to 50 characters long, not have leading or trailing spaces and not blank', tooltip = 'The first address line of the store. Appears on the receipt. Max 50 chars.' where name = 'addressLine1';
UPDATE data_element SET max_length = 50, validation_expression = '^[\\S](.{0,48})(\\S)$', validation_message = 'Must be up to 50 characters long, not have leading or trailing spaces and not blank', tooltip = 'The second address line of the store. Appears on the receipt. Max 50 chars.' where name = 'addressLine2';