update data_element as de inner join data_group as dg on dg.data_group_id = de.data_group_id set validation_expression = '^[A-Za-z0-9]{4}$', validation_message = 'Transaction retrieval site ID must be 4 alphanumeric chars', tooltip = 'Site ID sent in the transaction retrieval request. Must be 4 alphanumeric chars', max_length = 4 WHERE de.name = 'siteID' and dg.name = 'transactionRetrieval';