update data_element de inner join data_group as dg on dg.data_group_id = de.data_group_id SET de.validation_expression = '^[0-9]*$', de.validation_message = 'PIN code must be numeric only', de.tooltip = 'Sets the mode change PIN. This is used to switch a PED between modes. E.g. Standalone to EPOS. Must be Numeric value' WHERE de.name = 'modeChangePIN' and dg.name = 'userMgmt';
update data_element de inner join data_group as dg on dg.data_group_id = de.data_group_id SET de.validation_expression = '^[0-9]*$', de.validation_message = 'PIN code must be numeric only', de.tooltip = 'Sets the supervisor PIN. This is used to access the supervisor menu. 5 characters or more. Must be Numeric value' WHERE de.name = 'superPIN' and dg.name = 'userMgmt';
update data_element de inner join data_group as dg on dg.data_group_id = de.data_group_id SET de.validation_expression = '^[0-9]*$', de.validation_message = 'PIN code must be numeric only', de.tooltip = 'Set the PIN to allow access to the wifi settings menu on the PED. Must be Numeric value' WHERE de.name = 'wifiPIN' and dg.name = 'userMgmt';