UPDATE tid t JOIN tid_site ts ON  t.tid_id=ts.tid_id JOIN site_profiles sp ON sp.site_id=ts.site_id JOIN profile_data pd ON pd.profile_id=sp.profile_id JOIN data_element de ON de.data_element_id=pd.data_element_id JOIN data_group dg ON dg.data_group_id=de.data_group_id JOIN profile p ON p.profile_id = sp.profile_id JOIN profile_type pt ON p.profile_type_id = pt.profile_type_id SET eod_auto = IF(pd.datavalue="true",true,false) WHERE  dg.name="endOfDay" AND de.name ="auto";
