INSERT IGNORE INTO data_element(data_group_id, name, datatype, is_allow_empty, version, updated_at, updated_by, created_at, created_by, validation_expression, validation_message, front_end_validate, `unique`, options, displayname_en, is_encrypted, is_password, sort_order_in_group, required_at_site_level, tooltip, tid_overridable) VALUES ((SELECT data_group_id FROM data_group WHERE name = 'softUi'), 'NPCIQRpayloadValue', 'STRING', 1, 1, NOW(), 'system', NOW(), 'system','^[!@#$&()\\-`.+,/\A-Za-z0-9]*$', 'Please provide Any special character at starting of Input Value OR Alphanumeric', 0, 0, '', 'NPCI QR Payload Value', 0, 0, 4, 1, 'Enter Alphanumeric value which will be used in NPCI QR Payload',0);
INSERT IGNORE INTO data_element_locations_data_element(location_id, data_element_id) VALUES ((SELECT location_id FROM data_element_locations WHERE location_name = 'site_configuration'), (SELECT data_element_id FROM data_element WHERE name = 'NPCIQRpayloadValue' AND data_group_id = (SELECT data_group_id FROM data_group WHERE name = 'softUi')));