CREATE TABLE IF NOT EXISTS velocity_limits_txn (txn_limit_id VARCHAR(255) NOT NULL, velocity_limit_id VARCHAR(255) NOT NULL, limit_type INT NOT NULL, txn_type INT NOT NULL, limit_value INT NOT NULL, PRIMARY KEY (txn_limit_id), FOREIGN KEY (velocity_limit_id) REFERENCES velocity_limits(velocity_limit_id), FOREIGN KEY (limit_type) REFERENCES txn_limit_types(limit_type_id), FOREIGN KEY (txn_type) REFERENCES txn_types(txn_type_id));